import React, { useEffect, useState } from 'react';
import Card from '../components/Card';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function Home() {
  const [foodCat, setFoodCat] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const loadFoodItems = async () => {
    try {
      setLoading(true);
      const response = await fetch("http://localhost:5000/api/auth/foodData", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      setFoodItems(data[0]);
      setFoodCat(data[1]);
    } catch (error) {
      console.error("Error fetching food items:", error);
      setError("Failed to load food items. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadFoodItems();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <Navbar />
      <div>
        <div id="carouselExampleFade" className="carousel slide carousel-fade" data-bs-ride="carousel">
          <div className="carousel-inner" id='carousel'>
            <div className="carousel-caption" style={{ zIndex: "9" }}>
              <div className="d-flex justify-content-center">
                <input 
                  className="form-control me-2 w-75 bg-white text-dark" 
                  type="search" 
                  placeholder="Search in here..." 
                  aria-label="Search" 
                  value={search} 
                  onChange={(e) => setSearch(e.target.value)} 
                />
                <button className="btn text-white bg-danger" onClick={() => setSearch('')}>X</button>
              </div>
            </div>
            {/* Carousel Images */}
            {/* ... (unchanged) */}
          </div>
        </div>
      </div>
      <div className='container'>
        {foodCat.length > 0 ? foodCat.map((data) => (
          <div className='row mb-3' key={data.id}>
            <div className='fs-3 m-3'>{data.CategoryName}</div>
            <hr id="hr-success" style={{ height: "4px", backgroundImage: "-webkit-linear-gradient(left,rgb(0, 255, 137),rgb(0, 0, 0))" }} />
            {foodItems.length > 0 ? foodItems.filter(
              (items) => (items.CategoryName === data.CategoryName) && (items.name.toLowerCase().includes(search.toLowerCase()))
            ).map(filterItems => (
              <div key={filterItems.id} className='col-12 col-md-6 col-lg-3'>
                <Card foodName={filterItems.name} item={filterItems} options={filterItems.options[0]} ImgSrc={filterItems.img} />
              </div>
            )) : <div>No Such Data</div>}
          </div>
        )) : ""}
      </div>
      <Footer />
    </div>
  );
}
