import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setorderData] = useState({});

    const fetchMyOrder = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/auth/myOrderData", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ email: localStorage.getItem('userEmail') })
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            setorderData(data);
        } catch (error) {
            console.error("Error fetching order data: ", error);
        }
    };

    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <div>
            <Navbar />
            <div className='container'>
                <div className='row'>
                    {Object.keys(orderData).length > 0 ? (
                        orderData.order_data && orderData.order_data.length > 0 ? (
                            orderData.order_data.slice(0).reverse().map((order) => {
                                const orderDate = order[1];
                                const items = order[0];

                                return (
                                    <div key={orderDate}>
                                        <div className='m-auto mt-5'>
                                            <h4>{new Date(orderDate).toLocaleDateString()}</h4>
                                            <hr />
                                        </div>
                                        {items.map((item) => (
                                            <div className='col-12 col-md-6 col-lg-3' key={item.id}>
                                                <div className="card mt-3" style={{ width: "16rem", maxHeight: "360px" }}>
                                                    <img src={item.img} className="card-img-top" alt={item.name} style={{ height: "120px", objectFit: "fill" }} />
                                                    <div className="card-body">
                                                        <h5 className="card-title">{item.name}</h5>
                                                        <div className='container w-100 p-0' style={{ height: "38px" }}>
                                                            <span className='m-1'>{item.qty}</span>
                                                            <span className='m-1'>{item.size}</span>
                                                            <div className='d-inline ms-2 h-100 w-20 fs-5'>
                                                                â‚¹{item.price}/-
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                );
                            })
                        ) : (
                            <div>No orders available</div>
                        )
                    ) : (
                        <div>No orders available</div>
                    )}
                </div>
            </div>
            <Footer />
        </div>
    );
}
